#
# Structure for table "user"
#

DROP TABLE IF EXISTS `user`;
CREATE TABLE `user` (
  `userID` int(11) NOT NULL AUTO_INCREMENT COMMENT '用户编号',
  `isDel` int(11) NOT NULL DEFAULT '0' COMMENT '是否显示',
  `userDate` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '添加时间',
  `userName` varchar(50) NOT NULL DEFAULT '' COMMENT '用户名称',
  `userPassword` varchar(50) NOT NULL DEFAULT '' COMMENT '用户密码',
  PRIMARY KEY (`userID`),
  UNIQUE KEY `userID` (`userID`)
) ENGINE=MyISAM AUTO_INCREMENT=56 DEFAULT CHARSET=utf8 COMMENT='用户表';

#
# Data for table "user"
